import React, { useState } from "react";
import Records from "./Rec.json";

import "./App.css";

function App() {
  let stafDetiels = [
    {
      id: "58",
      name: "Mark Hill",
      designation: "Cheif Executive Officer",
      team: "Product",
      manager: null,
      img: "https://i.imgur.com/uk87y7L.jpg",
      short: "CEO",
    },
    {
      id: "59",
      name: "Joe Linux",
      designation: "Cheif Technology Officer",
      team: "Development & IT",
      manager: "58",
      img: "https://i.imgur.com/I8N6wle.gif",
      short: "CTO",
    },
    {
      id: "60",
      name: "Linda May",
      designation: "Cheif Bussiness Officer",
      team: "Bussiness Flow",
      manager: "58",
      img: "https://i.imgur.com/Id57NSW.jpg",
      short: "CBO",
    },
    {
      id: "61",
      name: "John Green",
      designation: "Cheif Accounting Officer",
      team: "Finance",
      manager: "58",
      img: "https://i.imgur.com/NRYrB0l.jpeg",
      short: "CAO",
    },
    {
      id: "62",
      name: "Ron Blomquist",
      designation: "Cheif Information Officer",
      team: "Development & IT",
      manager: "59",
      img: "https://i.imgur.com/Xdx7Ptn.jpeg",
      short: "CIO",
    },
    {
      id: "63",
      name: "Michael Rubin",
      designation: "Cheif Innovative Officer",
      team: "Development & IT",
      manager: "59",
      img: "https://i.imgur.com/eX7uYfG.jpeg",
      short: "CIO",
    },
    {
      id: "64",
      name: "Allice Lopez",
      designation: "Cheif Communication Officer",
      team: "Bussiness Flow",
      manager: "60",
      img: "https://i.imgur.com/waxFbz6.jpg",
      short: "CCO",
    },
    {
      id: "65",
      name: "Marry Johnson",
      designation: "Cheif Brand Officer",
      team: "Bussiness Flow",
      manager: "60",
      img: "https://i.imgur.com/Zi7qqQA.jpg",
      short: "CBO",
    },
    {
      id: "66",
      name: "Kirk Douglas",
      designation: "Cheif Bussiness Officer",
      team: "Bussiness Flow",
      manager: "60",
      img: "https://i.imgur.com/0hhtbTn.jpeg",
      short: "CBO",
    },
    {
      id: "67",
      name: "Erica Reel",
      designation: "Cheif Bussiness Officer",
      team: "Bussiness Flow",
      manager: "60",
      img: "https://i.imgur.com/I9XFqdd.jpg",
      short: "CBO",
    },
  ];

  let [input, setInput] = useState("");
  let [empid, setEmpid] = useState([]);
  let [teamstafDetiels, setTeamstafDetiels] = useState([]);

  let EmpstafDetiels = (e) => {
    setInput(e.target.value);
  };

  let onClick = () => {
    setEmpid(stafDetiels.filter((a) => a.id === input));
    setTeamstafDetiels(stafDetiels.filter((a) => a.manager === input));
  };
  return (
    <div className="App">
      <center>
        <h1>Manager Details</h1>
        <div>check the details by using dropdown</div>
        <br />
        <select onChange={EmpstafDetiels}>
          {stafDetiels.map((stafDetiels) => (
            <option>{stafDetiels.id}</option>
          ))}
        </select>
        <br />
        <button onClick={onClick}>Submit</button>

        <h2>Own stafDetiels</h2>
        <table border={"3"}>
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Designation</th>
              <th>Team</th>
              <th> Manager</th>
              <th>Img</th>
              <th>Short</th>
            </tr>
          </thead>
          <tbody>
            {empid.map((stafDetiels) => {
              return (
                <tr>
                  <td>{stafDetiels.id}</td>
                  <td>{stafDetiels.name}</td>
                  <td>{stafDetiels.designation}</td>
                  <td>{stafDetiels.team}</td>
                  <td>{stafDetiels.manager}</td>
                  <td>{stafDetiels.img}</td>
                  <td>{stafDetiels.short}</td>
                </tr>
              );
            })}
          </tbody>
        </table>

        <h2>Team stafDetiels</h2>
        <table border={"3"}>
          <thead>
            <th>ID</th>
            <th>Name</th>
            <th>Designation</th>
            <th>Team</th>
            <th> Manager</th>
            <th>Img</th>
            <th>Short</th>
          </thead>
          <tbody>
            {teamstafDetiels.map((stafDetiels) => {
              return (
                <tr>
                  <td>{stafDetiels.id}</td>
                  <td>{stafDetiels.name}</td>
                  <td>{stafDetiels.designation}</td>
                  <td>{stafDetiels.team}</td>
                  <td>{stafDetiels.manager}</td>
                  <td>{stafDetiels.img}</td>
                  <td>{stafDetiels.short}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </center>
    </div>
  );
}

export default App;
